
exe=../bin/etcd

nohup $exe --name node1 --data-dir=/tmp/etcd/node1/data --listen-client-urls http://0.0.0.0:12379 --advertise-client-urls http://127.0.0.1:12379 --listen-peer-urls http://0.0.0.0:12380 --initial-advertise-peer-urls http://127.0.0.1:12380 --initial-cluster node1=http://127.0.0.1:12380,node2=http://127.0.0.1:22380,node3=http://127.0.0.1:32380 --initial-cluster-token etcd-cluster-1 --initial-cluster-state new > /tmp/etcd/node1/log/log.msg 2>/tmp/etcd/node1/log/log.err &
nohup $exe --name node2 --data-dir=/tmp/etcd/node2/data --listen-client-urls http://0.0.0.0:22379 --advertise-client-urls http://127.0.0.1:22379 --listen-peer-urls http://0.0.0.0:22380 --initial-advertise-peer-urls http://127.0.0.1:22380 --initial-cluster node1=http://127.0.0.1:12380,node2=http://127.0.0.1:22380,node3=http://127.0.0.1:32380 --initial-cluster-token etcd-cluster-1 --initial-cluster-state new > /tmp/etcd/node2/log/log.msg 2>/tmp/etcd/node2/log/log.err &
nohup $exe --name node3 --data-dir=/tmp/etcd/node3/data --listen-client-urls http://0.0.0.0:32379 --advertise-client-urls http://127.0.0.1:32379 --listen-peer-urls http://0.0.0.0:32380 --initial-advertise-peer-urls http://127.0.0.1:32380 --initial-cluster node1=http://127.0.0.1:12380,node2=http://127.0.0.1:22380,node3=http://127.0.0.1:32380 --initial-cluster-token etcd-cluster-1 --initial-cluster-state new > /tmp/etcd/node3/log/log.msg 2>/tmp/etcd/node3/log/log.err &
